import{_ as a,c as s,o as e,a as n}from"./app.58320140.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":3,"title":"1.下载子系统","slug":"_1-下载子系统","link":"#_1-下载子系统","children":[]},{"level":3,"title":"2.安装必要软件","slug":"_2-安装必要软件","link":"#_2-安装必要软件","children":[]}],"relativePath":"Linux/子系统.md"}'),t={name:"Linux/子系统.md"},i=n(`<h3 id="_1-下载子系统" tabindex="-1">1.下载子系统 <a class="header-anchor" href="#_1-下载子系统" aria-hidden="true">#</a></h3><p><img src="https://upload-images.jianshu.io/upload_images/5415189-406b132777749578.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240" alt=""></p><p><img src="https://upload-images.jianshu.io/upload_images/5415189-759da722a6f05673.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240" alt=""></p><h3 id="_2-安装必要软件" tabindex="-1">2.安装必要软件 <a class="header-anchor" href="#_2-安装必要软件" aria-hidden="true">#</a></h3><p>0.<a href="https://www.linuxidc.com/Linux/2017-11/148627.htm" target="_blank" rel="noreferrer">更换软件源</a><a href="https://mirrors.tuna.tsinghua.edu.cn/help/ubuntu/" target="_blank" rel="noreferrer">https://mirrors.tuna.tsinghua.edu.cn/help/ubuntu/</a> 1.安装build-essential</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">sudo apt-get install build-essential</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><img src="https://upload-images.jianshu.io/upload_images/5415189-78cca69c80437e10.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240" alt=""></p><p>安装完之后发现gcc和g++都是5.4版本的，需要进行更新。 <img src="https://upload-images.jianshu.io/upload_images/5415189-077bb0fbf917c7cf.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240" alt=""></p><p>2.升级gcc, g++ 首先添加ppa到库</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">sudo add-apt-repository ppa:ubuntu-toolchain-r/test</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo apt-get update</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo apt-get install g++-8</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><img src="https://upload-images.jianshu.io/upload_images/5415189-67806fc2235cede0.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240" alt=""> 接下来使用管理员权限更新一下。</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">update-alternatives --install /usr/bin/gcc gcc /usr/bin/gcc-8 53 \\</span></span>
<span class="line"><span style="color:#A6ACCD;">--slave /usr/bin/g++ g++ /usr/bin/g++-8 \\</span></span>
<span class="line"><span style="color:#A6ACCD;">--slave /usr/bin/gcc-ar gcc-ar /usr/bin/gcc-ar-8 \\</span></span>
<span class="line"><span style="color:#A6ACCD;">--slave /usr/bin/gcc-nm gcc-nm /usr/bin/gcc-nm-8 \\</span></span>
<span class="line"><span style="color:#A6ACCD;">--slave /usr/bin/gcc-ranlib gcc-ranlib /usr/bin/gcc-ranlib-8</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p><img src="https://upload-images.jianshu.io/upload_images/5415189-42a28db55856a4fc.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240" alt=""></p><p>3.安装anaconda</p><p><a href="https://www.anaconda.com/distribution/" target="_blank" rel="noreferrer">官网</a>或者<a href="https://mirrors.tuna.tsinghua.edu.cn/help/anaconda/" target="_blank" rel="noreferrer">清华镜像</a>下载anaconda</p><p><img src="https://upload-images.jianshu.io/upload_images/5415189-e77fc35b6452e09e.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240" alt=""></p><p>配置环境变量</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">echo &#39;export PATH=&quot;~/anaconda3/bin:$PATH&quot;&#39; &gt;&gt; ~/.bashrc</span></span>
<span class="line"><span style="color:#A6ACCD;">source ~/.bashrc</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>安装成功 <img src="https://upload-images.jianshu.io/upload_images/5415189-bb6f3572fdf47e9f.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240" alt=""><img src="https://upload-images.jianshu.io/upload_images/5415189-824afee4b835411e.png?imageMogr2/auto-orient/strip%7CimageView2/2/w/1240" alt=""></p><p><a href="https://www.jianshu.com/p/fe327b72fa31" target="_blank" rel="noreferrer">https://www.jianshu.com/p/fe327b72fa31</a> 4.vscode配置终端</p>`,20),p=[i];function l(o,r,c,g,u,d){return e(),s("div",null,p)}const b=a(t,[["render",l]]);export{m as __pageData,b as default};
